#include <stdio.h>

int main() {
    long long n, m;
    scanf("%lld %lld", &n, &m);
    long long expected_time = 0;
    long long success_prob = 1;
    long long time_per_submission = 100 * (n - m) + 1900 * m;
    long long num_submissions = 0;

    while (success_prob > 1e-15) {
        num_submissions++;
        success_prob *= 0.5;
        expected_time += (long long)(time_per_submission * num_submissions * success_prob);

    }

    printf("%lld\n", expected_time);

    return 0;
}
