#include <iostream>

using namespace std;

int main() {
  long long n, m;
  cin >> n >> m;

  long long expected_time = 0;
  long long success_prob = 1;
  long long time_per_submission = 100 * (n - m) + 1900 * m;

  for (long long i = 1; ; ++i) {
    expected_time += (time_per_submission * i) * success_prob;
    if (m == 0) break;
    success_prob *= (long double)1/2;
    if (success_prob == 0) break;
    if (i >= 10000) break;
  }

  cout << expected_time << endl;
  return 0;
}
