import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        long ans = 0;
        for (int i = 0; i < (1 << n); i++) {
            long sum = 0;
            boolean[] smashed = new boolean[n];
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) {
                    for (int k = j; k < n; k++) {
                        if ((k + 1) % (j + 1) == 0) {
                            smashed[k] = true;
                        }
                    }
                }
            }
            for (int j = 0; j < n; j++) {
                if (!smashed[j]) {
                    sum += a[j];
                }
            }
            ans = Math.max(ans, sum);
        }
        System.out.println(ans);
    }
}
