#include <stdio.h>
#include <stdlib.h>

int cmp(const void *a, const void *b) {
    return *(int *)a - *(int *)b;
}

int main() {
    int n;
    scanf("%d", &n);
    int a[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    qsort(a, n, sizeof(int), cmp);
    int count = 0;
    int i = 0;
    int j = 0;
    while (i < n) {
        int num = a[i];
        int c = 0;
        while (j < n && a[j] == num) {
            j++;
            c++;
        }
        if (c > 0) {
            count++;
            i = j;
        }
    }

    if (count <= (n + 1) / 2)
    {
        printf("%d\n", count);
    } else {
        printf("%d\n", (n+1)/2);
    }


    return 0;
}
