#include <stdio.h>

long long solve() {
    long long x;
    scanf("%lld", &x);
    if (x <= 6) return 1;
    if (x % 14 == 0) return x / 14 * 4;
    if (x % 14 <= 6) return x / 14 * 4 + 1;
    return x / 14 * 4 + 2;

}

int main() {
    printf("%lld\n", solve());
    return 0;
}
