#include <stdio.h>
#include <stdlib.h>

int cmp(const void *a, const void *b) {
    return *(int *)a - *(int *)b;
}

int main() {
    int n;
    scanf("%d", &n);
    int a[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    qsort(a, n, sizeof(int), cmp);
    int count[100001] = {0};
    for (int i = 0; i < n; i++) {
        count[a[i]]++;
    }
    int ans = 0;
    int cur = 0;
    for (int i = 1; i <= 100000; i++) {
        if (count[i] > 0) {
            if (count[i] > (n - cur) / 2 + 1) {
                ans += (n - cur) / 2 + 1;
                cur += (n - cur) / 2 + 1;
            } else {
                ans += count[i];
                cur += count[i];
            }
        }
    }
    printf("%d\n", ans);
    return 0;
}
