#include <iostream>
#include <vector>
#include <map>

using namespace std;

int main() {
  int n;
  cin >> n;

  map<long long, int> counts;
  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
    counts[a[i]]++;
  }

  int ans = 0;
  for (auto const& [key, val] : counts) {
    ans++;
  }

  long long removed = 0;
  for (auto const& [key, val] : counts) {
    if (val > 1) {
      removed += val -1;
    }
  }

  if (removed % 2 != 0) {
      ans--;
  }


  cout << ans << endl;
  return 0;
}
