#include <stdio.h>

int main() {
    int N, M;
    scanf("%d %d", &N, &M);
    int l[N], r[N];
    for (int i = 0; i < N; i++) {
        scanf("%d %d", &l[i], &r[i]);
    }
    for (int d = 1; d <= M; d++) {
        int count = 0;
        for (int i = 0; i < N; i++) {
            int flag = 0;
            for (int j = 0; j <= M; j += d) {
                if (j >= l[i] && j <= r[i]) {
                    flag = 1;
                    break;
                }
            }
            if (flag == 1) {
                count++;
            }
        }
        printf("%d\n", count);
    }
    return 0;
}
