#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n, m;
  cin >> n >> m;

  vector<pair<long long, long long>> souvenirs(n);
  for (int i = 0; i < n; ++i) {
    cin >> souvenirs[i].first >> souvenirs[i].second;
  }

  for (int d = 1; d <= m; ++d) {
    vector<bool> purchased(n, false);
    int count = 0;
    for (long long i = 0; i <= m; i += d) {
      for (int j = 0; j < n; ++j) {
        if (i >= souvenirs[j].first && i <= souvenirs[j].second && !purchased[j]) {
          purchased[j] = true;
          count++;
        }
      }
    }
    cout << count << endl;
  }
  return 0;
}
