import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        int[][] souvenirs = new int[n][2];
        for (int i = 0; i < n; i++) {
            souvenirs[i][0] = sc.nextInt();
            souvenirs[i][1] = sc.nextInt();
        }
        for (int d = 1; d <= m; d++) {
            int count = 0;
            for (int i = 0; i < n; i++) {
                boolean found = false;
                for (int j = 0; j <= m; j += d) {
                    if (j >= souvenirs[i][0] && j <= souvenirs[i][1]) {
                        found = true;
                        break;
                    }
                }
                if (found) {
                    count++;
                }
            }
            System.out.println(count);
        }
    }
}
