n, m = map(int, input().split())
souvenirs = []
for i in range(n):
    l, r = map(int, input().split())
    souvenirs.append((l, r))

for d in range(1, m + 1):
    count = 0
    stops = set()
    for i in range(0, m + 1, d):
        stops.add(i)
    for l, r in souvenirs:
        can_buy = False
        for i in range(l, r + 1):
            if i in stops:
                can_buy = True
                break
        if can_buy:
            count += 1
    print(count)

