#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, k;
  cin >> n >> k;

  long long mod = 1e9 + 7;

  vector<vector<long long>> dp(n + 1, vector<long long>(n + 1, 0));
  dp[0][0] = 1;

  for (long long i = 1; i <= n; ++i) {
    for (long long j = 1; j <= i; ++j) {
      for (long long p = 0; p < i; ++p) {
        dp[i][j] = (dp[i][j] + dp[i - 1][p]) % mod;
        if (j > 1)
          dp[i][j] = (dp[i][j] + dp[i-1][j-1]) % mod;
      }

    }
  }

  long long ans = 0;
  if (k <= n) {
      ans = dp[n][k-1];
      for (int i = 0; i < n - k + 1; ++i) {
          ans = (ans * 2) % mod;
      }
  }


  cout << ans << endl;
  return 0;
}
