import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int k = sc.nextInt();
        long mod = 1000000007;
        long[][] dp = new long[n + 1][n + 1];
        dp[0][0] = 1;
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= i; j++) {
                for (int l = 0; l < j; l++) {
                    dp[i][j] = (dp[i][j] + dp[i - 1][l]) % mod;
                }
                for (int l = 0; l < i - j +1; l++) {
                    dp[i][j] = (dp[i][j] + dp[i-1][j-1+l]) % mod;

                }
            }
        }

        long ans = 0;
        for (int i = 0; i <= n ; i++) {
            ans = (ans + dp[n-1][i] * dp[n-1][k-1]) % mod;

        }
        System.out.println(ans);
    }
}
