#include <stdio.h>

int main() {
    long long n;
    scanf("%lld", &n);
    long long a[n], b[n];
    for (long long i = 0; i < n; i++) {
        scanf("%lld %lld", &a[i], &b[i]);
    }
    long long ans = 0;
    long long press[n];
    for (long long i = 0; i < n; i++) {
        long long need = (b[i] - (a[i] % b[i])) % b[i];
        press[i] = need;
        ans += need;
        for (long long j = i + 1; j < n; j++) {
            a[j] += need;
        }
    }
    printf("%lld\n", ans);
    return 0;
}
