#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n;
  cin >> n;

  vector<long long> a(n);
  vector<long long> b(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i] >> b[i];
  }

  long long ans = 0;
  vector<long long> presses(n, 0);
  for (long long i = 0; i < n; ++i) {
    long long num_presses = (b[i] - (a[i] % b[i])) % b[i];
    ans += num_presses;
    presses[i] = num_presses;
    for (long long j = i + 1; j < n; ++j) {
      a[j] += num_presses;
    }
  }

  cout << ans << endl;
  return 0;
}
