#include <stdio.h>
#include <stdlib.h>
#include <math.h>

int main() {
    int n;
    scanf("%d", &n);
    int *a = (int *)malloc((n - 1) * sizeof(int));
    for (int i = 0; i < n - 1; i++) {
        scanf("%d", &a[i]);
    }
    int max_depth = 0;
    int *depth = (int *)malloc(n * sizeof(int));
    for (int i = 0; i < n; i++) {
        depth[i] = 0;
    }
    for (int i = 0; i < n - 1; i++) {
        int current = i + 2;
        int d = 1;
        while (current != 1) {
            for (int j = 0; j < n - 1; j++) {
                if (a[j] == current) {
                    current = a[j] ;
                    break;
                }
            }
            d++;
        }
        depth[i+1] = d;
    }
    for (int i = 1; i < n; i++) {
        if (depth[i] > max_depth) {
            max_depth = depth[i];
        }
    }
    int result = (int)ceil(log2(n));
    printf("%d\n", max_depth);
    free(a);
    free(depth);
    return 0;
}
