import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[n - 1];
        for (int i = 0; i < n - 1; i++) {
            a[i] = sc.nextInt();
        }
        int maxDepth = 0;
        int[] depth = new int[n + 1];
        for (int i = 2; i <= n; i++) {
            int d = 1;
            int cur = a[i - 2];
            while (cur != 1) {
                boolean found = false;
                for (int j = 0; j < n - 1; j++) {
                    if (a[j] == cur) {
                        cur = a[j];
                        d++;
                        found = true;
                        break;
                    }
                }
                if (!found) break;
            }
            maxDepth = Math.max(maxDepth, d);
        }
        System.out.println(maxDepth);
    }
}
