import math

n = int(input())
a = []
for i in range(n - 1):
    a.append(int(input()))

depth = 0
counts = {}
for x in a:
    if x not in counts:
        counts[x] = 0
    counts[x] += 1

max_depth = 0
q = [1]
visited = {1}
d = {1:0}
while q:
    curr = q.pop(0)
    max_depth = max(max_depth, d[curr])
    for i in range(2, n+1):
        if i in counts and counts[i]>0 and a[i-2] == curr and i not in visited:
            q.append(i)
            visited.add(i)
            d[i] = d[curr]+1
            counts[i]-=1

print(max_depth+1)
