#include <stdio.h>

#define MOD 1000000007

int main() {
    long long n, a, b;
    scanf("%lld %lld %lld", &n, &a, &b);
    long long s[n];
    for (long long i = 0; i < n; i++) {
        scanf("%lld", &s[i]);
    }
    long long dp[n + 1][2];
    dp[0][0] = 1;
    dp[0][1] = 1;
    for (long long i = 1; i <= n; i++) {
        dp[i][0] = dp[i - 1][0];
        dp[i][1] = dp[i - 1][1];
        long long j = 0;
        while (j < i && s[i - 1] - s[j] < a) {
            j++;
        }
        if (j > 0)
            dp[i][0] = (dp[i][0] + dp[j - 1][1]) % MOD;
        j = 0;
        while (j < i && s[i - 1] - s[j] < b) {
            j++;
        }
        if (j > 0)
            dp[i][1] = (dp[i][1] + dp[j - 1][0]) % MOD;
    }
    printf("%lld\n", (dp[n][0] + dp[n][1]) % MOD);
    return 0;
}
