#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, a, b;
  cin >> n >> a >> b;

  vector<long long> s(n);
  for (int i = 0; i < n; ++i) {
    cin >> s[i];
  }

  long long count = 0;
  for (int i = 0; i < (1 << n); ++i) {
    vector<long long> x, y;
    for (int j = 0; j < n; ++j) {
      if ((i >> j) & 1) {
        x.push_back(s[j]);
      } else {
        y.push_back(s[j]);
      }
    }

    bool ok_x = true;
    for (int j = 0; j < x.size(); ++j) {
      for (int k = j + 1; k < x.size(); ++k) {
        if (abs(x[j] - x[k]) < a) {
          ok_x = false;
          break;
        }
      }
      if (!ok_x) break;
    }

    bool ok_y = true;
    for (int j = 0; j < y.size(); ++j) {
      for (int k = j + 1; k < y.size(); ++k) {
        if (abs(y[j] - y[k]) < b) {
          ok_y = false;
          break;
        }
      }
      if (!ok_y) break;
    }

    if (ok_x && ok_y) {
      count++;
    }
  }

  cout << count << endl;
  return 0;
}
