import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long a = sc.nextLong();
        long b = sc.nextLong();
        long[] s = new long[n];
        for (int i = 0; i < n; i++) {
            s[i] = sc.nextLong();
        }
        long mod = 1000000007;
        long[][] dp = new long[n + 1][2];
        dp[0][0] = 1;
        dp[0][1] = 1;
        for (int i = 0; i < n; i++) {
            dp[i + 1][0] = dp[i + 1][0] + dp[i][0];
            dp[i + 1][0] %= mod;
            boolean ok = true;
            for (int j = 0; j < i; j++) {
                if (s[i] - s[j] < a) {
                    ok = false;
                    break;
                }
            }
            if (ok) {
                dp[i + 1][0] = dp[i + 1][0] + dp[i][1];
                dp[i + 1][0] %= mod;
            }
            dp[i + 1][1] = dp[i + 1][1] + dp[i][1];
            dp[i + 1][1] %= mod;
            ok = true;
            for (int j = 0; j < i; j++) {
                if (s[i] - s[j] < b) {
                    ok = false;
                    break;
                }
            }
            if (ok) {
                dp[i + 1][1] = dp[i + 1][1] + dp[i][0];
                dp[i + 1][1] %= mod;
            }
        }
        System.out.println((dp[n][0] + dp[n][1]) % mod);
    }
}
