#include <iostream>
#include <map>

using namespace std;

int main() {
  int n;
  cin >> n;

  map<long long, int> d_count;
  for (int i = 0; i < n; ++i) {
    long long d;
    cin >> d;
    d_count[d]++;
  }

  int m;
  cin >> m;

  map<long long, int> t_count;
  for (int i = 0; i < m; ++i) {
    long long t;
    cin >> t;
    t_count[t]++;
  }

  bool possible = true;
  for (auto const& [key, val] : t_count) {
    if (d_count[key] < val) {
      possible = false;
      break;
    }
  }

  if (possible) {
    cout << "YES" << endl;
  } else {
    cout << "NO" << endl;
  }

  return 0;
}
