#include <stdio.h>
#include <stdlib.h>

#define MAXN 100005

int n;
int deg[MAXN];
int main() {
    scanf("%d", &n);
    for (int i = 0; i < n - 1; i++) {
        int u, v;
        scanf("%d %d", &u, &v);
        deg[u]++;
        deg[v]++;
    }
    int max_deg = 0;
    for (int i = 1; i <= n; i++) {
        if (deg[i] > max_deg) {
            max_deg = deg[i];
        }
    }
    int ans = 0;
    while ((1 << ans) < max_deg) {
        ans++;
    }
    printf("%d\n", ans);
    return 0;
}
