import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        List<List<Integer>> adj = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            adj.add(new ArrayList<>());
        }
        for (int i = 0; i < n - 1; i++) {
            int u = sc.nextInt() - 1;
            int v = sc.nextInt() - 1;
            adj.get(u).add(v);
            adj.get(v).add(u);
        }
        int maxDeg = 0;
        for (int i = 0; i < n; i++) {
            maxDeg = Math.max(maxDeg, adj.get(i).size());
        }
        int ans = maxDeg;
        int[] deg = new int[n];
        for (int i = 0; i < n; i++) {
            deg[i] = adj.get(i).size();
        }
        Arrays.sort(deg);
        int count = 1;
        for (int i = n - 2; i >= 0; i--) {
            if (deg[i] > count) {
                ans = Math.max(ans, count + 1);
            }
            count++;
        }

        System.out.println(ans);
    }
}
