#include <stdio.h>

int main() {
    long long n, m, k;
    scanf("%lld %lld %lld", &n, &m, &k);
    long long mod = 1e9 + 7;
    long long ans = 0;
    if (k == 2) {
        ans = n + m + 1;
    } else {
        long long num = n + m;
        long long den = k - 1;
        if (num % den != 0)
            return 0;
        ans = (num / den) + 1;

    }
    printf("%lld\n", ans % mod);
    return 0;
}
