#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, m, k;
  cin >> n >> m >> k;

  long long mod = 1e9 + 7;

  if (n == 0 && m == 0) {
    cout << 0 << endl;
    return 0;
  }

  if (n == 0) {
    cout << 1 << endl;
    return 0;
  }
  if (m == 0) {
    cout << 1 << endl;
    return 0;
  }


  long long ans = 0;
  if (k == 2) {
    ans = n + 1;
  } else {
    ans = (n + 1) * (m + 1) -1;
  }


  cout << (ans % mod + mod) % mod << endl;
  return 0;
}
