import java.util.*;
import java.math.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long m = sc.nextLong();
        long k = sc.nextLong();
        long mod = 1000000007;

        long ans = 0;
        if (k == 2) {
            ans = n + 1;
        } else {
            long num = n + m;
            long den = k -1;
            long rem = num % den;
            long quo = num / den;
            if(rem == 0){
                ans = quo + 1;
            } else {
                ans = 0;
            }



            long[][] C = new long[(int)(n+m+1)][(int)(n+m+1)];
            for(int i=0; i<=n+m; ++i){
                C[i][0] = 1;
                for(int j=1; j<=i; ++j){
                    C[i][j] = (C[i-1][j-1] + C[i-1][j]) % mod;
                }
            }


            long total = 0;
            for(int i=0; i<=n; ++i){
                total = (total + C[(int)(n+m)][(int)i]) % mod;
            }


            ans = total;
        }

        System.out.println(ans);
    }
}
