#include <stdio.h>
#include <string.h>

int main() {
    int n;
    scanf("%d", &n);
    char s[n + 1];
    scanf("%s", s);
    int x = 0, max_x = 0;
    for (int i = 0; i < n; i++) {
        if (s[i] == 'I') {
            x++;
        } else {
            x--;
        }
        if (x > max_x) {
            max_x = x;
        }
    }
    printf("%d\n", max_x);
    return 0;
}
