#include <iostream>
#include <string>
#include <algorithm>

using namespace std;

int main() {
    int n;
    cin >> n;
    string s;
    cin >> s;

    int x = 0;
    int max_x = 0;
    for (char c : s) {
        if (c == 'I') {
            x++;
        } else {
            x--;
        }
        max_x = max(max_x, x);
    }
    cout << max_x << endl;
    return 0;
}
