#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n;
  cin >> n;

  long long mod = 1e9 + 7;

  if (n == 0) {
    cout << 1 << endl;
    return 0;
  }

  vector<long long> primes;
  vector<long long> counts(1001, 0);

  for (long long i = 2; i <= n; ++i) {
    long long num = i;
    for (long long j = 2; j * j <= num; ++j) {
      while (num % j == 0) {
        bool found = false;
        for (long long k = 0; k < primes.size(); ++k) {
          if (primes[k] == j) {
            counts[j]++;
            found = true;
            break;
          }
        }
        if (!found) {
          primes.push_back(j);
          counts[j]++;
        }
        num /= j;
      }
    }
    if (num > 1) {
      bool found = false;
      for (long long k = 0; k < primes.size(); ++k) {
        if (primes[k] == num) {
          counts[num]++;
          found = true;
          break;
        }
      }
      if (!found) {
        primes.push_back(num);
        counts[num]++;
      }
    }
  }

  long long ans = 1;
  for (long long i = 0; i < primes.size(); ++i) {
    ans = (ans * (counts[primes[i]] + 1)) % mod;
  }

  cout << ans << endl;
  return 0;
}
