MOD = 10**9 + 7

def count_divisors(n):
    fact = 1
    for i in range(1, n + 1):
        fact *= i

    count = 0
    for i in range(1, int(fact**0.5) + 1):
        if fact % i == 0:
            count += 1
            if i * i != fact:
                count += 1
    return count % MOD

n = int(input())
print(count_divisors(n))

