#include <stdio.h>
#include <stdlib.h>

long long min(long long a, long long b) {
    return a < b ? a : b;
}

int main() {
    long long n, a, b;
    scanf("%lld %lld %lld", &n, &a, &b);
    long long x[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld", &x[i]);
    }
    long long dp[n];
    dp[0] = 0;
    for (int i = 1; i < n; i++) {
        dp[i] = dp[i - 1] + a * (x[i] - x[i - 1]);
        long long cost = dp[i];
        for (int j = 0; j < i; j++) {
            cost = min(cost, dp[j] + b + a * (x[i] - x[j]));

        }
        dp[i] = cost;
    }
    printf("%lld\n", dp[n - 1]);
    return 0;
}
