#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  long long n, a, b;
  cin >> n >> a >> b;

  vector<long long> x(n);
  for (int i = 0; i < n; ++i) {
    cin >> x[i];
  }

  long long min_fatigue = -1;

  for (int i = 0; i < (1 << (n - 1)); ++i) {
    long long current_fatigue = 0;
    long long current_pos = x[0];
    vector<int> visited;
    visited.push_back(0);

    for (int j = 0; j < n - 1; ++j) {
      if ((i >> j) & 1) { 
        current_fatigue += b;
        current_pos = x[j + 1];
        visited.push_back(j+1);
      } else {
        current_fatigue += a * abs(x[j + 1] - current_pos);
        current_pos = x[j + 1];
        visited.push_back(j+1);
      }
    }

    if (min_fatigue == -1 || current_fatigue < min_fatigue) {
      min_fatigue = current_fatigue;
    }
  }
  cout << min_fatigue << endl;
  return 0;
}
