#include <stdio.h>

#define MOD 1000000007

long long power(long long base, long long exp) {
    long long res = 1;
    base %= MOD;
    while (exp > 0) {
        if (exp % 2 == 1) res = (res * base) % MOD;
        base = (base * base) % MOD;
        exp /= 2;
    }
    return res;
}

int main() {
    int n;
    scanf("%d", &n);

    long long ans = 1;
    long long primes[1001];
    int primeCount = 0;
    bool isPrime[1001];
    for (int i = 0; i <= 1000; i++) isPrime[i] = true;
    isPrime[0] = isPrime[1] = false;

    for (int p = 2; p <= 1000; p++) {
        if (isPrime[p]) {
            primes[primeCount++] = p;
            for (int i = p * p; i <= 1000; i += p)
                isPrime[i] = false;
        }
    }

    for (int i = 0; i < primeCount; i++) {
        long long p = primes[i];
        long long count = 0;
        long long tmp = n;
        while (tmp > 0) {
            count += tmp / p;
            tmp /= p;
        }
        ans = (ans * (count + 1)) % MOD;
    }
    printf("%lld\n", ans);
    return 0;
}
