#include <iostream>
#include <vector>

using namespace std;

long long power(long long base, long long exp, long long mod) {
  long long res = 1;
  base %= mod;
  while (exp > 0) {
    if (exp % 2 == 1) res = (res * base) % mod;
    base = (base * base) % mod;
    exp /= 2;
  }
  return res;
}

int main() {
  long long n;
  cin >> n;

  long long mod = 1e9 + 7;
  vector<long long> prime_counts(n + 1, 0);

  for (long long i = 2; i <= n; ++i) {
    long long temp = i;
    for (long long j = 2; j * j <= temp; ++j) {
      while (temp % j == 0) {
        prime_counts[j]++;
        temp /= j;
      }
    }
    if (temp > 1) {
      prime_counts[temp]++;
    }
  }

  long long ans = 1;
  for (long long i = 2; i <= n; ++i) {
      if (prime_counts[i] > 0)
          ans = (ans * (prime_counts[i] + 1)) % mod;
  }

  cout << ans << endl;
  return 0;
}
