import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long mod = 1000000007;
        long[] prime = new long[n + 1];
        for (int i = 2; i <= n; i++) {
            if (prime[i] == 0) {
                for (int j = i; j <= n; j += i) {
                    int count = 0;
                    int temp = j;
                    while (temp > 0) {
                        count += temp / i;
                        temp /= i;
                    }
                    prime[j] += count;
                }
            }
        }
        long ans = 1;
        for (int i = 2; i <= n; i++) {
            if (prime[i] > 0) {
                ans = (ans * (prime[i] + 1)) % mod;
            }
        }
        System.out.println(ans);
    }
}
