MOD = 10**9 + 7

def count_divisors(n):
    prime_factors = {}
    i = 2
    temp_n = n
    while i * i <= temp_n:
        while temp_n % i == 0:
            prime_factors[i] = prime_factors.get(i, 0) + 1
            temp_n //= i
        i += 1
    if temp_n > 1:
        prime_factors[temp_n] = prime_factors.get(temp_n, 0) + 1

    ans = 1
    for count in prime_factors.values():
        ans = (ans * (count + 1)) % MOD
    return ans

