import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long a = sc.nextLong();
        long b = sc.nextLong();
        long[] x = new long[n];
        for (int i = 0; i < n; i++) {
            x[i] = sc.nextLong();
        }
        long[] dp = new long[n];
        Arrays.fill(dp, Long.MAX_VALUE);
        dp[0] = 0;
        for (int i = 0; i < n - 1; i++) {
            if (dp[i] == Long.MAX_VALUE) continue;
            for (int j = i + 1; j < n; j++) {
                long cost = dp[i] + (x[j] - x[i]) * a;
                if (cost < dp[j]) {
                    dp[j] = cost;
                }
            }
            for (int j = i + 1; j < n; j++) {
                long cost = dp[i] + b;
                if (cost < dp[j]) {
                    dp[j] = cost;
                }

            }
        }
        System.out.println(dp[n - 1]);
    }
}
