#include <stdio.h>
#include <stdlib.h>
#include <stdbool.h>

#define MAXN 100005

int n, m;
int a[MAXN], b[MAXN];
bool adj[MAXN][MAXN];

int main() {
    scanf("%d %d", &n, &m);
    for (int i = 0; i < m; i++) {
        scanf("%d %d", &a[i], &b[i]);
        adj[a[i]][b[i]] = adj[b[i]][a[i]] = true;
    }
    int count = 0;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            if (i == j) continue;
            if (adj[i][j]) continue;
            bool found = false;
            for (int k = 1; k <= n; k++) {
                if (adj[i][k]) {
                    for (int l = 1; l <= n; l++) {
                        if (adj[k][l]) {
                            for (int p = 1; p <= n; p++) {
                                if (adj[l][p] && p == j) {
                                    found = true;
                                    break;
                                }
                            }
                        if (found) break;
                        }
                    if (found) break;
                    }
                if (found) break;
                }
            }
            if (found) {
                adj[i][j] = adj[j][i] = true;
                count++;
            }
        }
    }
    printf("%d\n", count);
    return 0;
}
