#include <stdio.h>

#define MOD 1000000007

long long dp[1001][1001];
long long comb[1001][1001];

int main() {
    int n, a, b, c, d;
    scanf("%d %d %d %d %d", &n, &a, &b, &c, &d);

    for (int i = 0; i <= n; i++) {
        comb[i][0] = 1;
        for (int j = 1; j <= i; j++) {
            comb[i][j] = (comb[i - 1][j - 1] + comb[i - 1][j]) % MOD;
        }
    }

    dp[0][0] = 1;
    for (int i = 1; i <= n; i++) {
        for (int j = 0; j <= i; j++) {
            for (int k = a; k <= b && k <= i; k++) {
                for (int l = c; l <= d && l * k <= i; l++) {
                    if (i >= k * l) {
                        dp[i][j + l] = (dp[i][j + l] + dp[i - k * l][j] * comb[i -1][k -1] % MOD * comb[i - k][k * l - 1] % MOD) % MOD;

                    }
                }
            }
        }
    }

    long long ans = 0;
    for (int i = 0; i <= n; i++) {
        ans = (ans + dp[n][i]) % MOD;
    }

    printf("%lld\n", ans);
    return 0;
}
