import java.util.*;

public class Main {
    static final int MOD = 1000000007;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int a = sc.nextInt();
        int b = sc.nextInt();
        int c = sc.nextInt();
        int d = sc.nextInt();

        long[][] dp = new long[n + 1][n + 1];
        dp[0][0] = 1;

        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= i; j++) {
                for (int k = a; k <= b && k <= i; k++) {
                    if (i - k >= 0) {
                        dp[i][j] = (dp[i][j] + dp[i - k][j - 1]) % MOD;
                    }
                }
            }
        }

        long ans = 0;
        for (int i = 1; i <= n; i++) {
            long count = 0;
            for (int j = 1; j <= n; j++) {
                if (dp[n][i] > 0 && i >= c && i <= d) {
                    boolean possible = true;
                    int[] groupSizes = new int[i];
                    Arrays.fill(groupSizes,0);
                    int tempN = n;
                    for(int k=1; k<=i; k++){
                        if(tempN >= a && tempN <= b){
                            groupSizes[k-1] = tempN;
                            tempN = 0;
                        }
                    }
                    count++;
                }
            }
            ans = (ans + count) % MOD;
        }

        System.out.println(ans);
        sc.close();
    }
}
