#include <stdio.h>
#include <stdlib.h>

#define MAXN 5005
#define MAXM 205

long long a[MAXN];
long long b[MAXN][MAXM];
long long dp[MAXN][1 << MAXM];

int main() {
    int n, m;
    scanf("%d %d", &n, &m);
    for (int i = 1; i < n; i++) {
        scanf("%lld", &a[i]);
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            scanf("%lld", &b[i][j]);
        }
    }
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j < (1 << m); j++) {
            dp[i][j] = -1e18;
        }
    }
    dp[1][0] = 0;
    for (int i = 1; i <= n; i++) {
        for (int mask = 0; mask < (1 << m); mask++) {
            if (dp[i][mask] == -1e18) continue;
            for (int next_mask = 0; next_mask < (1 << m); next_mask++) {
                if ((mask | next_mask) == mask) continue;
                long long cur_deliciousness = 0;
                for (int j = 0; j < m; j++) {
                    if ((next_mask >> j) & 1) {
                        cur_deliciousness += b[i][j + 1];
                    }
                }
                for (int next = i + 1; next <= n; next++) {
                    long long dist = 0;
                    for (int k = i; k < next; k++) {
                        dist += a[k];
                    }
                    dp[next][mask | next_mask] =
                        (dp[next][mask | next_mask] > dp[i][mask] + cur_deliciousness - dist) ?
                        dp[next][mask | next_mask] : dp[i][mask] + cur_deliciousness - dist;
                }
            }
        }
    }
    long long ans = -1e18;
    for (int i = 1; i <= n; i++) {
        ans = (ans > dp[i][(1 << m) - 1]) ? ans : dp[i][(1 << m) - 1];
    }
    printf("%lld\n", ans);
    return 0;
}
