#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n, m;
  cin >> n >> m;

  vector<long long> a(n - 1);
  for (int i = 0; i < n - 1; ++i) {
    cin >> a[i];
  }

  vector<vector<long long>> b(n, vector<long long>(m));
  for (int i = 0; i < n; ++i) {
    for (int j = 0; j < m; ++j) {
      cin >> b[i][j];
    }
  }

  long long max_happiness = -1e18; 

  for (int i = 0; i < (1 << (n * m)); ++i) {
    long long current_happiness = 0;
    long long current_distance = 0;
    vector<bool> used_tickets(m, false);
    int current_restaurant = 0;
    vector<int> restaurant_sequence;
    restaurant_sequence.push_back(0);

    for (int j = 0; j < n * m; ++j) {
      if ((i >> j) & 1) {
        int restaurant_index = j / m;
        int ticket_index = j % m;

        if (!used_tickets[ticket_index]) {
          if (restaurant_index >= restaurant_sequence.size()){
              bool possible = false;
              for (int k = 0; k < restaurant_sequence.size(); k++){
                  if (restaurant_index > restaurant_sequence[k]){
                      possible = true;
                      break;
                  }
              }
              if(!possible) continue;
          }

          current_happiness += b[restaurant_index][ticket_index];
          used_tickets[ticket_index] = true;

          if (restaurant_index > 0){
            int dist_index = 0;
            for (int k = 0; k < restaurant_index; k++){
                dist_index += (restaurant_sequence[k+1] > restaurant_sequence[k]) ? 1 : -1;
            }
            for (int k = 0; k < abs(dist_index); k++){
                current_distance += a[k];
            }
          }

          restaurant_sequence.push_back(restaurant_index);
        }
      }
    }

    if (count(used_tickets.begin(), used_tickets.end(), false) == 0) {
      max_happiness = max(max_happiness, current_happiness - current_distance);
    }
  }

  cout << max_happiness << endl;

  return 0;
}
