#include <iostream>

using namespace std;

int main() {
  long long k, s;
  cin >> k >> s;

  long long count = 0;
  for (long long x = 0; x <= k; ++x) {
    for (long long y = 0; y <= k; ++y) {
      long long z = s - x - y;
      if (z >= 0 && z <= k) {
        count++;
      }
    }
  }
  cout << count << endl;
  return 0;
}
