#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main() {
    int sx, sy, tx, ty;
    scanf("%d %d %d %d", &sx, &sy, &tx, &ty);
    char *path = (char *)malloc(10000 * sizeof(char));
    int dx = tx - sx;
    int dy = ty - sy;
    for (int i = 0; i < dy; i++) {
        strcat(path, "U");
    }
    for (int i = 0; i < dx; i++) {
        strcat(path, "R");
    }
    for (int i = 0; i < dy; i++) {
        strcat(path, "D");
    }
    for (int i = 0; i < dx; i++) {
        strcat(path, "L");
    }
    for (int i = 0; i < dy + 1; i++) {
        strcat(path, "U");
    }
    for (int i = 0; i < dx + 1; i++) {
        strcat(path, "R");
    }
    for (int i = 0; i < dy + 1; i++) {
        strcat(path, "D");
    }
    for (int i = 0; i < dx + 1; i++) {
        strcat(path, "L");
    }
    printf("%s\n", path);
    free(path);
    return 0;
}
