#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

#define INF INT_MAX

int main() {
    int n, m;
    scanf("%d %d", &n, &m);

    int edges[m][3];
    for (int i = 0; i < m; i++) {
        scanf("%d %d %d", &edges[i][0], &edges[i][1], &edges[i][2]);
        edges[i][0]--; 
        edges[i][1]--; 
    }

    int dist[n][n];
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            dist[i][j] = (i == j) ? 0 : INF;
        }
    }

    for (int i = 0; i < m; i++) {
        dist[edges[i][0]][edges[i][1]] = dist[edges[i][1]][edges[i][0]] = edges[i][2];
    }

    for (int k = 0; k < n; k++) {
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (dist[i][k] != INF && dist[k][j] != INF && dist[i][k] + dist[k][j] < dist[i][j]) {
                    dist[i][j] = dist[i][k] + dist[k][j];
                }
            }
        }
    }

    int count = 0;
    for (int i = 0; i < m; i++) {
        int u = edges[i][0];
        int v = edges[i][1];
        int w = edges[i][2];
        int shortest = dist[u][v];
        if (shortest != w)
            count++;
        else{
            int flag =0;
            for(int j=0; j<n; ++j){
                for(int k=0; k<n; ++k){
                    if(j==k) continue;
                    if(dist[j][k] == dist[j][u] + w + dist[v][k] && dist[j][u] != INF && dist[v][k] != INF){
                        flag = 1;
                        break;
                    }
                     if(dist[j][k] == dist[j][v] + w + dist[u][k] && dist[j][v] != INF && dist[u][k] != INF){
                        flag = 1;
                        break;
                    }
                }
                if(flag) break;
            }
            if(!flag) count++;
        }
    }

    printf("%d\n", count);

    return 0;
}
