#include <stdio.h>
#include <stdlib.h>

int main() {
    long long x, y;
    scanf("%lld %lld", &x, &y);
    if (x == y) {
        printf("0\n");
        return 0;
    }
    long long ans1 = abs(x - y);
    long long ans2 = abs(x + y) + 1;
    printf("%lld\n", (ans1 < ans2) ? ans1 : ans2);
    return 0;
}
