import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long x = sc.nextLong();
        long y = sc.nextLong();
        long ans = 0;
        if (x == y) {
            System.out.println(0);
            return;
        }
        if (x == -y) {
            System.out.println(1);
            return;
        }
        if (x > 0 && y > 0) {
            ans = Math.abs(x - y);
        } else if (x < 0 && y < 0) {
            ans = Math.abs(x - y);
        } else if (x > 0 && y < 0) {
            ans = Math.abs(x + y) + 1;
        } else {
            ans = Math.abs(x + y) + 1;
        }
        System.out.println(ans);
    }
}
