    long long a[n];
    for (long long i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
    }
    long long dp[n + 1];
    for (long long i = 0; i <= n; i++) {
        dp[i] = -1e18; 
    }
    dp[0] = 0;
    for (long long i = 1; i <= n; i++) {
        for (long long j = i - 1; j >= 0; j--) {
            if (i - j >= k) {
                long long sum = 0;
                for (long long l = j; l < i; l++) {
                    sum += a[l];
                }
                dp[i] = (dp[i] > dp[j] + sum) ? dp[i] : dp[j] + sum;
                dp[i] = (dp[i] > dp[j]) ? dp[i] : dp[j];
            }
        }
    }
    long long ans = 0;
    for (long long i = 0; i <=n; i++) {
        ans = (ans > dp[i]) ? ans : dp[i];
    }
    printf("%lld\n", ans);
    return 0;
}
