#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, k;
  cin >> n >> k;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long max_score = 0;
  for (int i = 0; i < (1 << n); ++i) {
    vector<long long> current_a = a;
    vector<bool> colored(n, false);
    long long current_score = 0;
    bool possible = true;

    for (int j = 0; j < n; ++j) {
      if ((i >> j) & 1) {
        bool painted = false;
        for (int l = max(0, j - k + 1); l <= j; ++l) {
          bool all_in_range = true;
          for (int m = l; m <= j; ++m) {
            if (m >= n) {
              all_in_range = false;
              break;
            }
          }
          if (all_in_range) {
            bool can_paint = true;
            for (int m = l; m <=j; ++m){
              if(colored[m]){
                can_paint = false;
                break;
              }
            }
            if(can_paint){
              for (int m = l; m <= j; ++m) {
                colored[m] = true;
                current_a[m] = (current_a[m] > 0 ? current_a[m] : 0);
              }
              painted = true;
              break;
            }
          }
        }
        if (!painted) {
          possible = false;
          break;
        }
      }
    }
    if (possible) {
      for (int j = 0; j < n; ++j) {
        if ((i >> j) & 1) {
          current_score += current_a[j];
        }
      }
      max_score = max(max_score, current_score);
    }
  }
  cout << max_score << endl;
  return 0;
}
